# Split the mappings into lines and separate main category and subcategory using '-'
lines = mappings.split('\t')
data = [line.strip().split(' - ') for line in lines]
# Create a dictionary to store data for each category
categories = {}
subCategory = None
counter = 0
for item in data:
    if len(item) == 2:
        for cat in item:
            if counter % 2 == 0: 
                subCategory = cat
            elif counter%2 == 1:
                if cat not in categories.keys():
                    categories[cat]=[subCategory]
                else:
                    categories[cat].append(subCategory)
            counter +=1
# Convert the dictionary into a pandas DataFrame
# Find the maximum length of lists in the dictionary
max_len = max(len(sub_categories) for sub_categories in categories.values())

# Fill in the missing values with empty strings for each key to make lists of equal length

for key in categories:
    i = 0
    origLen = len(categories[key])
    while i < max_len-origLen:
        categories[key].append(" ")
        i += 1
    categories[key] += [''] * (max_len - len(categories[key]))

# Convert the dictionary into a pandas DataFrame
df = pd.DataFrame(categories)

# Save the DataFrame to a CSV file
df.to_csv("data/categories.csv", index=False)
